// rs - the ResultSet instance
// xmlElement - a string containing the document level xml element to use for the XML document
function resultSetToXml(rs, xmlElement) {
  var rsmd = rs.getMetaData();
  var result = new XML(xmlElement);

  for (var i = 1; i <= rsmd.getColumnCount(); i++)  {
	var value = rs.getString(i);

    if (value === "null") {
      value = null;
    }

    result[rsmd.getColumnName(i).toLowerCase()] = value;
  }

  return result;
}

function addExternalInmigrations(visitId, dbConn) {
  var results = dbConn.executeCachedQuery("SELECT _URI, REASON, INDIVIDUAL_INFO_DATE_OF_BIRTH, DATE_OF_MIGRATION, INDIVIDUAL_INFO_FIRST_NAME, INDIVIDUAL_INFO_MOTHER_ID, INDIVIDUAL_INFO_INDIVIDUAL_ID, MOVEDFROM, OPENHDS_VISIT_ID, INDIVIDUAL_INFO_GENDER, OPENHDS_LOCATION_ID, OPENHDS_FIELD_WORKER_ID, INDIVIDUAL_INFO_LAST_NAME, INDIVIDUAL_INFO_PARTIAL_DATE, INDIVIDUAL_INFO_FATHER_ID, INDIVIDUAL_INFO_MIDDLE_NAME FROM " + globalMap.get("IN_MIGRATION_TABLE") + " WHERE OPENHDS_VISIT_ID = '" + visitId + "' AND openhds_migration_type = 'EXTERNAL_INMIGRATION' AND processed_by_mirth=0 order by INDIVIDUAL_INFO_DATE_OF_BIRTH ASC");
  var inMigrationForms = resultsToForms(results, "<inmigration event='external-inmigration' table-name='" + globalMap.get("IN_MIGRATION_TABLE") + "'/>");
  return inMigrationForms;
}

function markRecordAsProcessedMigration(rawData, status) {
  var originalXml = new XML(rawData);
  // retrieve the table to update
  var tableName = originalXml["@table-name"].toString();
  var id = originalXml["id"].toString();

 var dbConn = DatabaseConnectionFactory.createDatabaseConnection('com.mysql.jdbc.Driver', globalMap.get("HRS_DATABASE_URL"), globalMap.get("HRS_DATABASE_USER"), globalMap.get("HRS_DATABASE_PASSWORD"));
  var result = dbConn.executeUpdate("UPDATE " + tableName + " SET processed_by_mirth = " + status + " WHERE id = '" + id + "'");
  dbConn.close();
}

function markRecordAsProcessed(rawData, status) {
  var originalXml = new XML(rawData);
  // retrieve the table to update
  var tableName = originalXml["@table-name"].toString();
  var uri = originalXml["_uri"].toString();


if (tableName == globalMap.get("VISIT_TABLE") ) {
	var dbConn = DatabaseConnectionFactory.createDatabaseConnection('com.mysql.jdbc.Driver', globalMap.get("ODK_DATABASE_URL"), globalMap.get("ODK_DATABASE_USER"), globalMap.get("ODK_DATABASE_PASSWORD"));
    var result = dbConn.executeUpdate("UPDATE " + tableName + " SET processed_by_mirth = " + status + " WHERE OPENHDS_VISIT_ID = '" + originalXml["openhds_visit_id"].toString() + "'");
    dbConn.close();
} else if (tableName !== "OMGIM") {
 var dbConn = DatabaseConnectionFactory.createDatabaseConnection('com.mysql.jdbc.Driver', globalMap.get("ODK_DATABASE_URL"), globalMap.get("ODK_DATABASE_USER"), globalMap.get("ODK_DATABASE_PASSWORD"));
  var result = dbConn.executeUpdate("UPDATE " + tableName + " SET processed_by_mirth = " + status + " WHERE _URI = '" + uri + "'");
  dbConn.close();
  } 
}

function addInternalInmigrations(visitId, dbConn) {
  var results = dbConn.executeCachedQuery("SELECT _URI, REASON, DATE_OF_MIGRATION, INDIVIDUAL_INFO_INDIVIDUAL_ID, ORIGIN, OPENHDS_VISIT_ID, OPENHDS_LOCATION_ID, OPENHDS_FIELD_WORKER_ID FROM " + globalMap.get("IN_MIGRATION_TABLE") + " WHERE OPENHDS_VISIT_ID = '" + visitId + "' AND openhds_migration_type = 'INTERNAL_INMIGRATION' AND processed_by_mirth=0");
  var inMigrationForms = resultsToForms(results, "<inmigration event='internal-inmigration' table-name='" + globalMap.get("IN_MIGRATION_TABLE") + "'/>");
  return inMigrationForms;
}

