var forms = new java.util.ArrayList()

var dbConn = DatabaseConnectionFactory.createDatabaseConnection('com.mysql.jdbc.Driver', globalMap.get("HRS_DATABASE_URL"), globalMap.get("HRS_DATABASE_USER"), globalMap.get("HRS_DATABASE_PASSWORD"));

var round = dbConn.executeCachedQuery("select id, ROUND_NUM, str_to_date(START_DATE,'%Y-%m-%d') START_DATE, str_to_date(END_DATE,'%Y-%m-%d') END_DATE, REMARKS  FROM round where processed_by_mirth=0");

forms.addAll(resultsToForms(round, "<round event='round' table-name='round'/>"));


var location = dbConn.executeCachedQuery("SELECT id, lowestLocationId , LOCATIONID, FIELDWORKER, 'RUR' LOCATION_TYPE, ACCURACY, ALTITUDE, LONGITUDE, LATITUDE FROM location WHERE processed_by_mirth =0");

forms.addAll(resultsToForms(location, "<location event='location' table-name='location'/>"));

checkLocation = dbConn.executeCachedQuery("SELECT id FROM location WHERE processed_by_mirth =0");
if (checkLocation.next()) {
  dbConn.close();
  return forms;
}

var visitResult = dbConn.executeCachedQuery("SELECT id, str_to_date(DATE,'%Y-%m-%d') DATE, OBSERVEID, LOCATIONID, FIELDWORKER, ROUND FROM observation WHERE processed_by_mirth=0 LIMIT 100;");
forms.addAll(resultsToForms(visitResult, "<visit event='visit' table-name='observation'/>"));

checkVisit = dbConn.executeCachedQuery("SELECT id FROM observation WHERE processed_by_mirth =0");
if (checkVisit.next()) {
  dbConn.close();
  return forms;
}
var individualresults = dbConn.executeCachedQuery("SELECT id, str_to_date(BIRTH_DATE,'%Y-%m-%d') BIRTH_DATE, FIRSTNAME,MIDDLENAME, MOTHERID, INDIVIDID,  GENDER, FIELDWORKER, LASTNAME, approximate, FATHERID FROM individual WHERE processed_by_mirth=0 order by BIRTH_DATE ASC LIMIT 100;");
forms.addAll(resultsToForms(individualresults, "<individual event='individual' table-name='individual'/>"));

checkIndividual = dbConn.executeCachedQuery("SELECT id FROM individual WHERE processed_by_mirth =0");
if (checkIndividual.next()) {
  dbConn.close();
  return forms;
}

var householdResult = dbConn.executeCachedQuery("select id, str_to_date(DE_DATE,'%Y-%m-%d') DE_DATE , SOCIALGPID , FIELDWORKER , NAME, TYPE , HEADID  FROM socialgroup WHERE processed_by_mirth=0;");
forms.addAll(resultsToForms(householdResult, "<household event='household' table-name='socialgroup'/>"));

checkHousehold = dbConn.executeCachedQuery("SELECT id FROM socialgroup WHERE processed_by_mirth =0");
if (checkHousehold.next()) {
  dbConn.close();
  return forms;
}
var relationshipResult = dbConn.executeCachedQuery("SELECT id, str_to_date(DE_DATE,'%Y-%m-%d') DE_DATE, str_to_date(SDATE,'%Y-%m-%d') SDATE, INDIVIDID2, FIELDWORKER, INDIVIDID, TYPE FROM relationship WHERE processed_by_mirth =0");
forms.addAll(resultsToForms(relationshipResult, "<relationship event='relationship' table-name='relationship'/>"));

checkRelationship = dbConn.executeCachedQuery("SELECT id FROM relationship WHERE processed_by_mirth =0");
if (checkRelationship.next()) {
  dbConn.close();
  return forms;
}
var membershipResult = dbConn.executeCachedQuery("SELECT id, str_to_date(DE_DATE,'%Y-%m-%d') DE_DATE, str_to_date(SDATE,'%Y-%m-%d') SDATE, FIELDWORKER, SOCIALGPID, RLTN_HEAD, INDIVIDID,SEVENTTYPE,EEVENTTYPE,str_to_date(EDATE,'%Y-%m-%d') EDATE FROM membership WHERE processed_by_mirth =0 order by SDATE asc LIMIT 100;");
forms.addAll(resultsToForms(membershipResult, "<membership event='membership' table-name='membership'/>"));

checkMembership = dbConn.executeCachedQuery("SELECT id FROM membership WHERE processed_by_mirth =0");
if (checkMembership.next()) {
  dbConn.close();
  return forms;
}

var pregnobsresults = dbConn.executeCachedQuery("SELECT id, OBSERVEID,DATE_ADD(str_to_date(DATE,'%Y-%m-%d'), INTERVAL 9- CAST(PREG_MONTH AS SIGNED) MONTH) EXPECTED_DELIVERY_DATE, FIELDWORKER,str_to_date(DATE,'%Y-%m-%d') DATE, INDIVIDID FROM indvstatus WHERE processed_by_mirth =0");
forms.addAll(resultsToForms(pregnobsresults, "<pregnancyobs event='pregnancyobs' table-name='indvstatus'/>"));

checkPregnObs = dbConn.executeCachedQuery("SELECT id FROM indvstatus WHERE processed_by_mirth =0");
if (checkPregnObs.next()) {
  dbConn.close();
  return forms;
}


 var resultsOutcome = dbConn.executeCachedQuery("SELECT id, OBSERVEID, INDIVIDID, 1 PARTIAL_DATE,FIELDWORKER,str_to_date(DATE,'%Y-%m-%d') DATE, FATHERID FROM pregoutcome WHERE processed_by_mirth =0");
  while(resultsOutcome.next()) {
    var pregOut = resultSetToXml(resultsOutcome, "<pregnancyoutcome event='pregnancyoutcome' table-name='pregoutcome' />");
    // are there any outcomes for this pregnancy outcome?
    var outcomes = dbConn.executeCachedQuery("SELECT FIRSTNAME, TYPE, SOCIALGPID , GENDER, 9 RELATIONSHIP_TO_GROUP_HEAD, a.INDIVIDID INDIVIDID, LASTNAME FROM birth a, individual b where a.INDIVIDID = b.INDIVIDID and OBSERVEID= '" + pregOut["observeid"].toString() + "'");
    while(outcomes.next()) {
      var outcome = resultSetToXml(outcomes, "<outcome />");
      pregOut.appendChild(outcome);
    }

    forms.add(pregOut.toString());
  }

checkPregnOut = dbConn.executeCachedQuery("SELECT id FROM pregoutcome WHERE processed_by_mirth =0");
if (checkPregnOut.next()) {
  dbConn.close();
  return forms;
}


var residencyResult = dbConn.executeCachedQuery("SELECT id, str_to_date(DE_DATE,'%Y-%m-%d') DE_DATE, str_to_date(SDATE,'%Y-%m-%d') SDATE, FIELDWORKER, LOCATIONID,  INDIVIDID,SEVENTTYPE,EEVENTTYPE,str_to_date(EDATE,'%Y-%m-%d') EDATE FROM residency WHERE processed_by_mirth =0 order by SDATE asc LIMIT 100;");
forms.addAll(resultsToForms(residencyResult, "<residency event='residency' table-name='residency'/>"));

checkResidency = dbConn.executeCachedQuery("SELECT id FROM residency WHERE processed_by_mirth =0");
if (checkResidency.next()) {
  dbConn.close();
  return forms;
}

var deathresults = dbConn.executeCachedQuery("SELECT id, OBSERVEID, REASON, FIELDWORKER, str_to_date(DATE,'%Y-%m-%d') DATE, PLACE, INDIVIDID FROM death where processed_by_mirth =0");
forms.addAll(resultsToForms(deathresults, "<death event='death' table-name='death'/>"));

checkDeath = dbConn.executeCachedQuery("SELECT id FROM death WHERE processed_by_mirth =0");
if (checkDeath.next()) {
  dbConn.close();
  return forms;
}
var omgresults = dbConn.executeCachedQuery("SELECT id, OBSERVEID, REGION_NAM, FIELDWORKER, str_to_date(DATE,'%Y-%m-%d') DATE, REASON, INDIVIDID FROM outmigration WHERE processed_by_mirth =0 LIMIT 100");
forms.addAll(resultsToForms(omgresults, "<outmigration event='outmigration' table-name='outmigration' />"));

checkOmg = dbConn.executeCachedQuery("SELECT id FROM outmigration WHERE processed_by_mirth =0");
if (checkOmg.next()) {
  dbConn.close();
  return forms;
}

var imgresults = dbConn.executeCachedQuery("SELECT id, REASON, str_to_date(DATE,'%Y-%m-%d') DATE, INDIVIDID, REGION_NAM MOVED_FROM , OBSERVEID, INTERNAL, LOCATIONID, FIELDWORKER FROM inmigration where processed_by_mirth=0 LIMIT 100");
forms.addAll(resultsToForms(imgresults, "<inmigration event='inmigration' table-name='inmigration'/>"));

dbConn.close();

return forms;

