// converts to OpenHDS pregnancy outcome XML
// basic fields
tmp['collectedBy']['extId'] = msg['fieldworker'].toString().toUpperCase();
tmp['visit']['extId'] = msg['observeid'].toString().toUpperCase();
tmp['outcomeDate'] = msg['date'].toString();
tmp['mother']['extId'] = msg['individid'].toString().toUpperCase();
tmp['father']['extId'] = msg['fatherid'].toString().toUpperCase();


// outcomes
var outcomesXml = new XML("<outcomes />");
var outcomes = msg['outcome'].length();
for(var i = 0; i < outcomes; i++) {
  outcomesXml.appendChild(new XML("<outcome />"));
  if (msg['outcome'][i]['type'].toString() == "LBR") {
    outcomesXml['outcome'][i]['type'] = msg['outcome'][i]['type'].toString();
    outcomesXml['outcome'][i]['childextId'] = msg['outcome'][i]['individid'].toString();
/*    outcomesXml['outcome'][i]['child']['extId'] = msg['outcome']['individid'].toString();
    outcomesXml['outcome'][i]['child']['firstName'] = msg['outcome'][i]['firstname'].toString();
    outcomesXml['outcome'][i]['child']['lastName'] = msg['outcome'][i]['lastname'].toString();
    outcomesXml['outcome'][i]['child']['gender'] = msg['outcome'][i]['gender'].toString();
    outcomesXml['outcome'][i]['child']['partialDate'] = msg['partial_date'].toString();
    outcomesXml['outcome'][i]['childMembership']['socialGroup']['extId'] = msg['outcome'][i]['socialgpid'].toString()
	outcomesXml['outcome'][i]['childMembership']['bIsToA'] = msg['outcome'][i]['relationship_to_group_head'].toString();*/
  } else {
    outcomesXml['outcome'][i]['type'] = msg['outcome'][i]['type'].toString();
  }
}

tmp["outcomes"] = outcomesXml;

