channel.postprocessingScript:
// This script executes once after a message has been processed

// update the database record to mark it as being processed by mirth
var status=1;

var i = responseMap.size();
for ( var k=0; k<i; k++) {
	var j=k+1;
	connectorMessage = message.getConnectorMessages().get(j);
	if (responseMap.get("d"+j).getStatus().toString()=="ERROR") {
		status=2;
	    globalMap.put("channelError", connectorMessage.getConnectorName());
	    globalMap.put("requestError", responseMap.get("d"+j).getMessage());
		globalMap.put("transformed", connectorMessage.getTransformedData());


	}
	markRecordAsProcessedMigration(connectorMessage.getRawData(),status);

}
return;

